-- ----------------------------
-- Procedure structure for hlc_BrowseDoctors
-- ----------------------------
CREATE PROCEDURE [hlc_BrowseDoctors]
    @HospitalID int,
    @SpecialtyID int,
    @Attitude int = -1,
    @Alpha varchar(1) = ''
AS 

If @HospitalID = -1 AND @SpecialtyID = -1 

  -- ANY HOSPITAL, ANY SPECIALTY. The @Alpha must be passed in or
  -- be blank.  This most often is the ressult of clicking one of the 
  -- letter buttons and not selecting anything in the dropdowns on the 
  -- left. Check below to see if we are searching on an @Attitude
  --
  BEGIN
      
      IF @Attitude <> -1

        -- Specific Attitude, any Hosplital or Specialty
        --
        BEGIN    
            SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
                   hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
                   hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
	           hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
                   FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
                   ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
	           dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
            FROM   hlc_Doctor left outer JOIN 
                   hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
            WHERE  (hlc_Doctor.LastName like (@Alpha +'%')) 
              and  hlc_Doctor.Attitude = @Attitude
            ORDER BY hlc_Doctor.LastName
        END

      ELSE

        -- Any Attitude, any Hosplital or Specialty
        --
        BEGIN
            SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
                   hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
                   hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
	           hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
                   FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
                   ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
	           dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
            FROM   hlc_Doctor left outer JOIN 
                   hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
            WHERE  (hlc_Doctor.LastName like (@Alpha +'%')) 
            ORDER BY hlc_Doctor.LastName
        END
  END

ELSE

  -- SPECIFIC HOSPITAL AND/OR SPECIFIC ATTITUDE.

  BEGIN
      IF @SpecialtyID <> -1 AND @HospitalID = -1
        BEGIN
            select * from hlc_Doctor
        END
      ELSE
        IF @SpecialtyID = -1 AND @HospitalID <> -1
          BEGIN
              select * from hlc_Doctor
          END
        ELSE
           IF @SpecialtyID <> -1 AND @HospitalID <> -1
             BEGIN
                 select * from hlc_Doctor
             END
  END

GO

-- ----------------------------
-- Procedure structure for hlc_BrowseDoctorsForHospital
-- ----------------------------
CREATE procedure [hlc_BrowseDoctorsForHospital]
   @HospitalID int
as
  select ID, FirstName + ' ' + LastName as FullName, LastName, HomePhone, MobilePhone, Pager, Attitude,
  case Attitude when 1 then 'Favorable' when 2 then 'Limitations' when 3 then 'Not Favorable' when 4 then 'Cooperative' else 'Unknown' end as AttitudeText
  from hlc_Doctor
  where ID in (select DoctorID from hlc_DoctorHospital where HospitalID=@HospitalID)
  order by LastName

GO

-- ----------------------------
-- Procedure structure for hlc_BrowseDoctorsForPractice
-- ----------------------------
CREATE procedure [hlc_BrowseDoctorsForPractice]
   @PracticeID int
as
  select ID, FirstName + ' ' + LastName as FullName, LastName, HomePhone, MobilePhone, Pager, Attitude,
  case Attitude when 1 then 'Favorable' when 2 then 'Limitations' when 3 then 'Not Favorable' else 'Unknown' end as AttitudeText
  from hlc_Doctor
  where PracticeID=@PracticeID
  order by LastName

GO

-- ----------------------------
-- Procedure structure for hlc_BrowseHospitals
-- ----------------------------
CREATE PROCEDURE [hlc_BrowseHospitals] AS

select ID, HospitalName, City, State, OfficePhone1, Case When HasBSMP=1 then 'Yes' Else 'No' End as BSMP, BSMPFirstName + ' ' + BSMPLastName As BSMPName, BSMPMobilePhone,
(select count(ID) from hlc_DoctorHospital where hlc_DoctorHospital.HospitalID = hlc_Hospital.ID) as NumDocs
from hlc_Hospital
order by HospitalName

