CREATE PROCEDURE [hlc_DeleteDoctor] 
  @ID int
AS

  -- Remove all dependent child records for this Doctor from the various tables
  --
  delete from hlc_DoctorHospital  where DoctorID = @ID
  delete from hlc_DoctorSpecialty where DoctorID = @ID
  delete from hlc_DoctorNote      where DoctorID = @ID

  -- Now get rid of this Doctor
  --
  delete from hlc_Doctor where ID = @ID

GO

