-- ----------------------------
-- Procedure structure for hlc_GetDoctorsByAlpha
-- ----------------------------
CREATE PROCEDURE [hlc_GetDoctorsByAlpha] 
    @Alpha char(1),
    @Attitude int = -1
AS

  -- NOTE: If any changes are made to the SQL below, please make the same changes in the
  --       hlc_GetDoctorsByHospitalIDSpecialtyID stored proc as well!    

if @Attitude <> -1
  
  -- We are restricting to a specific Attitude
  --
  begin    
  -- Limit search based on first letter of last name
  --
  IF @Alpha <> 'X'

    SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
           hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
           hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
	   hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
           FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
           ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
	   dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
    FROM   hlc_Doctor left outer JOIN 
           hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
    WHERE  (hlc_Doctor.LastName like (@Alpha +'%')) 
      and  hlc_Doctor.Attitude = @Attitude
    ORDER BY hlc_Doctor.LastName

  ELSE

    SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
           hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
           hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
	   hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
           FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
           ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
	   dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
    FROM   hlc_Doctor left outer JOIN 
           hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
    WHERE  ((hlc_Doctor.LastName like 'X%') OR (hlc_Doctor.LastName like 'Y%') OR (hlc_Doctor.LastName like 'Z%'))
      and  hlc_Doctor.Attitude = @Attitude
    ORDER BY hlc_Doctor.LastName
  end

else

  begin
  IF @Alpha <> 'X'

    SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
           hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
           hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
	   hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
           FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
           ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
	   dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
    FROM   hlc_Doctor left outer JOIN 
           hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
    WHERE  (hlc_Doctor.LastName like (@Alpha +'%')) 
    ORDER BY hlc_Doctor.LastName

  ELSE

    SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
           hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
           hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
	   hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
           FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
           ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
	   dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
    FROM   hlc_Doctor left outer JOIN 
           hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
    WHERE  (hlc_Doctor.LastName like 'X%') OR (hlc_Doctor.LastName like 'Y%') OR (hlc_Doctor.LastName like 'Z%')
    ORDER BY hlc_Doctor.LastName
  end

GO

-- ----------------------------
-- Procedure structure for hlc_GetDoctorsByHospitalIDSpecialtyID
-- ----------------------------
CREATE PROCEDURE [hlc_GetDoctorsByHospitalIDSpecialtyID] 
    @HospitalID int,
    @SpecialtyID int,
    @Attitude int = -1
AS

-- NOTE: If any changes are made to the SQL below, please make the same changes in the
--       hlc_GetDoctorsByAlpha stored proc as well!    

IF @HospitalID = -1 and @SpecialtyID <> -1
  BEGIN
    
    IF @Attitude = -1
	  -- Just limit search based on SpecialtyID (all Hospitals included, All Attitudes)
	  --
	  SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
	         hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
	         hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
		 hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
	         FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
	         ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
		 dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
	  FROM   hlc_Doctor left outer JOIN 
	         hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID  INNER JOIN
	         hlc_DoctorSpecialty ON hlc_Doctor.ID = hlc_DoctorSpecialty.DoctorID
	  WHERE  (hlc_DoctorSpecialty.SpecialtyID=@SpecialtyID) 
	  ORDER BY hlc_Doctor.LastName

    ELSE
	  -- Limit search based on SpecialtyID and Attitude (all Hospitals included)
	  --
	  SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
	         hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
	         hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
		 hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
	         FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
	         ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
		 dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
	  FROM   hlc_Doctor left outer JOIN 
	         hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID  INNER JOIN
	         hlc_DoctorSpecialty ON hlc_Doctor.ID = hlc_DoctorSpecialty.DoctorID
	  WHERE  (hlc_DoctorSpecialty.SpecialtyID=@SpecialtyID) 
            AND  (hlc_Doctor.Attitude=@Attitude)
	  ORDER BY hlc_Doctor.LastName

  END

ELSE

  BEGIN

    IF @Attitude = -1
	  -- Search based on both HospitalID and SpecialtyID (All Attitudes)
	  --
	  SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
	         hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
	         hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
		 hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
	         FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
	         ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,           
		 dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
	  FROM   hlc_Doctor left outer JOIN 
	         hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID INNER JOIN                     
	         hlc_DoctorHospital ON hlc_Doctor.ID = hlc_DoctorHospital.DoctorID INNER JOIN
	         hlc_DoctorSpecialty ON hlc_Doctor.ID = hlc_DoctorSpecialty.DoctorID  
	  WHERE  (hlc_DoctorHospital.HospitalID=@HospitalID) AND (hlc_DoctorSpecialty.SpecialtyID = @SpecialtyID) 
	  ORDER BY hlc_Doctor.LastName
    
    ELSE
	  -- Search based on HospitalID and SpecialtyID and Attitudes
	  --
	  SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
	         hlc_Practice.PracticeName, hlc_Practice.Address1, hlc_Practice.City, hlc_Practice.State, hlc_Practice.Zip,
	         hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
		 hlc_Doctor.Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,
	         FavChildNonEmergency,NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,
	         ConsultChildEmergency, dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,           
		 dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
	  FROM   hlc_Doctor left outer JOIN 
	         hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID INNER JOIN                     
	         hlc_DoctorHospital ON hlc_Doctor.ID = hlc_DoctorHospital.DoctorID INNER JOIN
	         hlc_DoctorSpecialty ON hlc_Doctor.ID = hlc_DoctorSpecialty.DoctorID  
	  WHERE  (hlc_DoctorHospital.HospitalID=@HospitalID) 
             AND (hlc_DoctorSpecialty.SpecialtyID=@SpecialtyID) 
             AND (hlc_Doctor.Attitude=@Attitude)
	  ORDER BY hlc_Doctor.LastName

  END

GO

