CREATE PROCEDURE [hlc_InsertDoctor]
  @ID int OUT,
  @FirstName varchar(50),
  @LastName varchar(50),
  @MobilePhone varchar(12),
  @HomePhone varchar(12),
  @Pager varchar(12),
  @EmailAddress varchar(80),
  @Attitude char(1),
  @FavAdultEmergency bit,
  @FavAdultNonEmergency bit,
  @NotFavAdult bit,
  @FavChildEmergency bit,
  @FavChildNonEmergency bit,
  @NotFavChild bit,
  @AcceptsMedicaid bit,
  @ConsultAdultEmergency bit,
  @ConsultChildEmergency bit,
  @DateLastUpdated smalldatetime,
  @LastUpdatedBy varchar(12),
  @PracticeID int,
  @PracticeName varchar(80),
  @Address1 varchar(80),
  @Address2 varchar(80),
  @City varchar(50),
  @State char(2),
  @Zip varchar(10),
  @OfficePhone1 varchar(12),
  @OfficePhone2 varchar(12),
  @Fax varchar(12),
  @HospID1 int,
  @HospNotes1 varchar(512),
  @HospID2 int,
  @HospNotes2 varchar(512),
  @HospID3 int,
  @HospNotes3 varchar(512),
  @HospID4 int,
  @HospNotes4 varchar(512),
  @HospID5 int,
  @HospNotes5 varchar(512),
  @HospID6 int,
  @HospNotes6 varchar(512),
  @SpecID1 int,
  @SpecArea1 varchar(512),
  @SpecID2 int,
  @SpecArea2 varchar(512),
  @SpecID3 int,
  @SpecArea3 varchar(512),
  @SpecID4 int,
  @SpecArea4 varchar(512),
  @SpecID5 int,
  @SpecArea5 varchar(512),
  @SpecID6 int,
  @SpecArea6 varchar(512),
  @Status int,
  @StatusDate smalldatetime,
  @IsBSMP bit,
  @RegContacted int,
  @SpecificallyKnown int,
  @Helpful int,
  @FrequentlyTreat int,
  @TreatYears int,
  @IsHRP bit,
  @PeerReview varchar(120)

AS

  -- If PracticeID passed in is -1, this means that we are creating a new Practice using the values passed
  -- in via the Practice fields (these values are only used when PracticeID = -1).  Create the Practice 
  -- first and capture the ID of the Practice
  --
  if @PracticeID = -1
    begin
	
	insert into hlc_Practice (PracticeName, Address1, Address2, City, State, Zip, OfficePhone1)
          values (@PracticeName, @Address1, @Address2, @City, @State, @Zip, @OfficePhone1)
	

	set @PracticeID = @@identity	

    end

  -- Insert the base Doctor information
  --
  insert into hlc_Doctor (FirstName, LastName, MobilePhone, HomePhone, Pager, EmailAddress,
                          Attitude,FavAdultEmergency,FavAdultNonEmergency,NotFavAdult,FavChildEmergency,FavChildNonEmergency,
                          NotFavChild,AcceptsMedicaid,ConsultAdultEmergency,ConsultChildEmergency,DateLastUpdated,
                          LastUpdatedBy,PracticeID,Status,StatusDate,IsBSMP,RegContacted,SpecificallyKnown,Helpful,
                          FrequentlyTreat,TreatYears,IsHRP,PeerReview)
                  values (@FirstName,@LastName,@MobilePhone,@HomePhone,@Pager,@EmailAddress,
                          @Attitude,@FavAdultEmergency,@FavAdultNonEmergency,@NotFavAdult,@FavChildEmergency,@FavChildNonEmergency,
                          @NotFavChild,@AcceptsMedicaid,@ConsultAdultEmergency,@ConsultChildEmergency,@DateLastUpdated,
                          @LastUpdatedBy,@PracticeID,@Status,@StatusDate,@IsBSMP,@RegContacted,@SpecificallyKnown,@Helpful,
                          @FrequentlyTreat,@TreatYears,@IsHRP,@PeerReview)

  -- Now insert the DoctorHospitals and DoctorSpecialties, but only where a value is passed in
  --
  begin
    -- Grab the newest DoctorID just inserted
    --declare @DoctorID int
    set @ID = @@identity

    if @HospID1 <> 0
      insert into hlc_DoctorHospital (DoctorID,HospitalID,Notes) values (@ID,@HospID1,@HospNotes1)
    if @HospID2 <> 0
      insert into hlc_DoctorHospital (DoctorID,HospitalID,Notes) values (@ID,@HospID2,@HospNotes2)
    if @HospID3 <> 0
      insert into hlc_DoctorHospital (DoctorID,HospitalID,Notes) values (@ID,@HospID3,@HospNotes3)
    if @HospID4 <> 0
      insert into hlc_DoctorHospital (DoctorID,HospitalID,Notes) values (@ID,@HospID4,@HospNotes4)
    if @HospID5 <> 0
      insert into hlc_DoctorHospital (DoctorID,HospitalID,Notes) values (@ID,@HospID5,@HospNotes5)
    if @HospID6 <> 0
      insert into hlc_DoctorHospital (DoctorID,HospitalID,Notes) values (@ID,@HospID6,@HospNotes6)

    if @SpecID1 <> 0
      insert into hlc_DoctorSpecialty (DoctorID,SpecialtyID,AreaOfExpertise) values (@ID,@SpecID1,@SpecArea1)
    if @SpecID2 <> 0
      insert into hlc_DoctorSpecialty (DoctorID,SpecialtyID,AreaOfExpertise) values (@ID,@SpecID2,@SpecArea2)
    if @SpecID3 <> 0
      insert into hlc_DoctorSpecialty (DoctorID,SpecialtyID,AreaOfExpertise) values (@ID,@SpecID3,@SpecArea3)
    if @SpecID4 <> 0
      insert into hlc_DoctorSpecialty (DoctorID,SpecialtyID,AreaOfExpertise) values (@ID,@SpecID4,@SpecArea4)
    if @SpecID5 <> 0
      insert into hlc_DoctorSpecialty (DoctorID,SpecialtyID,AreaOfExpertise) values (@ID,@SpecID5,@SpecArea5)
    if @SpecID6 <> 0
      insert into hlc_DoctorSpecialty (DoctorID,SpecialtyID,AreaOfExpertise) values (@ID,@SpecID6,@SpecArea6)
     

  end

GO

