-- Procedure structure for hlc_RptDoctorsSpecAttitude
-- ----------------------------
CREATE PROCEDURE [hlc_RptDoctorsSpecAttitude]
  @Specialties varchar(1000),
  @Attitudes varchar(1000) 
AS
/*
  Revisions:
  11-02-2015   Initial version
*/
BEGIN

DECLARE @SpecTable TABLE (id INT)
DECLARE @AttTable TABLE (id INT)

IF @Specialties = ''
 INSERT INTO @SpecTable SELECT ID FROM hlc_Specialty
ELSE
 INSERT INTO @SpecTable SELECT ID FROM dbo.fn_CSVToTable(@Specialties)

IF @Attitudes = ''
 INSERT INTO @AttTable SELECT ID FROM hlc_Attitude
ELSE
 INSERT INTO @AttTable SELECT ID FROM dbo.fn_CSVToTable(@Attitudes)

-- Doctor by Specialty and Attitude using drop down selection of specialty with ability for multi-selection 
-- and drop down selection of attitude. (list Selection Criteria; Doctor's name; Practice Name; contact info; 
-- Hospital associated with) 
 SELECT hlc_Doctor.ID AS DoctorID, hlc_Doctor.FirstName, hlc_Doctor.LastName, 
    hlc_Practice.PracticeName,
    hlc_Doctor.MobilePhone, hlc_Doctor.Pager, hlc_Doctor.HomePhone, hlc_Practice.OfficePhone1,
    dbo.hlc_AttitudeText(hlc_Doctor.Attitude) AS Attitude, 
    dbo.hlc_GetSpecialties(hlc_Doctor.ID) AS Specialties,    
    dbo.hlc_GetHospitals(hlc_Doctor.ID) as Hospitals
 FROM      hlc_Doctor 
    LEFT JOIN hlc_Practice ON hlc_Doctor.PracticeID = hlc_Practice.ID 
 WHERE hlc_Doctor.Attitude IN (SELECT id FROM @AttTable)
    AND hlc_Doctor.ID IN (SELECT ds.DoctorID 
                          FROM hlc_DoctorSpecialty ds 
        WHERE ds.SpecialtyID In (SELECT id FROM @SpecTable ) 
                         )
 ORDER BY hlc_Doctor.LastName
END


GO

