(@Attitude smallint)
RETURNS varchar(20)
AS
BEGIN
declare @Return varchar(20)
select @Return = case @Attitude
    when 0 then 'Unknown'
    when 1 then 'Cooperative'
    when 2 then 'Favorable'
    when 3 then 'Limitations'
    when 4 then 'NOT Favorable'
    when 5 then 'Out of Area Consultant'
    else '???'
    end

return @return
end



